create table tb_course (id int8 generated by default as identity, created_at TIMESTAMP WITHOUT TIME ZONE, description TEXT, img_uri varchar(255), name varchar(255), primary key (id));
create table tb_deliver (id int8 generated by default as identity, created_at TIMESTAMP WITHOUT TIME ZONE, feedback varchar(255), status int4, uri varchar(255), course_id int8, lesson_id int8, user_id int8, primary key (id));
create table tb_lesson (id int8 generated by default as identity, status int4, subtitle varchar(255), title varchar(255), course_id int8, primary key (id));
create table tb_role (id int8 generated by default as identity, authority varchar(255), primary key (id));
create table tb_user (id int8 generated by default as identity, email varchar(255), name varchar(255), password varchar(255), university varchar(255), primary key (id));
create table tb_user_course (user_id int8 not null, course_id int8 not null, primary key (user_id, course_id));
create table tb_user_role (user_id int8 not null, role_id int8 not null, primary key (user_id, role_id));
